mapper.select:
select
  fix_id, user_id, fix_name, fix_state, fix_message
from fix


        and fix_id = #{fixId}


        and user_id = #{userId}


        and fix_name = #{fixName}


        and fix_state = #{fixState}


        and fix_message = #{fixMessage}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from fix


        and fix_id = #{fixId}


        and user_id = #{userId}


        and fix_name = #{fixName}


        and fix_state = #{fixState}


        and fix_message = #{fixMessage}
