mapper.select:
select
  permit_id, apply_id, permit_state, permit_time
from permit


        and permit_id = #{permitId}


        and apply_id = #{applyId}


        and permit_state = #{permitState}


        and permit_time = #{permitTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from permit


        and permit_id = #{permitId}


        and apply_id = #{applyId}


        and permit_state = #{permitState}


        and permit_time = #{permitTime}
