mapper.select:
select
  code_id, user_id, code_image, valid_time
from qrcode


        and code_id = #{codeId}


        and user_id = #{userId}


        and code_image = #{codeImage}


        and valid_time = #{validTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from qrcode


        and code_id = #{codeId}


        and user_id = #{userId}


        and code_image = #{codeImage}


        and valid_time = #{validTime}
