mapper.select:
select
  user_id, code_id, type_id, user_name, user_gender, user_type, user_image, user_number, user_date, user_state
from user


        and user_id = #{userId}


        and code_id = #{codeId}


        and type_id = #{typeId}


        and user_name = #{userName}


        and user_gender = #{userGender}


        and user_type = #{userType}


        and user_image = #{userImage}


        and user_number = #{userNumber}


        and user_date = #{userDate}


        and user_state = #{userState}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and user_id = #{userId}


        and code_id = #{codeId}


        and type_id = #{typeId}


        and user_name = #{userName}


        and user_gender = #{userGender}


        and user_type = #{userType}


        and user_image = #{userImage}


        and user_number = #{userNumber}


        and user_date = #{userDate}


        and user_state = #{userState}
