mapper.select:
select
  permit_id, user_id, permit_state, permit_time
from permit


        and permit_id = #{permitId}


        and user_id = #{userId}


        and permit_state = #{permitState}


        and permit_time = #{permitTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from permit


        and permit_id = #{permitId}


        and user_id = #{userId}


        and permit_state = #{permitState}


        and permit_time = #{permitTime}
